/* eslint-disable no-unused-expressions */
import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import AddBoxIcon from "@mui/icons-material/AddBox";
import LogoutIcon from "@mui/icons-material/Logout";
import { LoginIcon } from "@mui/icons-material";
import { Button } from "@mui/material";

const Header = () => {
  const [userName, setUserName] = useState(null);
  const { userInfo, setUserInfo } = useContext(userContext);
  useEffect(() => {
    fetch("http://localhost:4000/profile", { credentials: "include" }).then(
      (response) => {
        response.json().then((userInfo) => {
          if (userInfo) {
            setUserName(userInfo.username);
          }
        });
      }
    );
  }, []);

  const logout = () => {
    fetch("http://localhost:4000/logout", {
      method: "POST",
      credentials: "include",
    });
    setUserName(null);
  };

  return (
    <header className="flex justify-between mx-[66px] my-3">
      <Link to="/" className="font-bold text-xl">
        My Blog
      </Link>
      <nav className="space-x-5 font-semibold text-xl">
        {userName && (
          <div className="flex gap-x-2">
            <Link
              to="/create"
              className="items-center justify-center text-center "
            >
              <Button
                endIcon={<AddBoxIcon />}
                variant="outlined"
                color="success"
              >
                create
              </Button>
            </Link>
            <Button
              startIcon={<LogoutIcon />}
              onClick={logout}
              color="error"
              variant="contained"
              disableElevation
              className="w-[30px] text-center text-sm"
            ></Button>
          </div>
        )}
        {!userName && (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>
    </header>
  );
};

export default Header;
