import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
const Content = () => {
  const { id } = useParams();
  const [post, setPost] = useState({});

  useEffect(() => {
    fetch(`http://localhost:4000/post/${id}`)
      .then((response) =>
        response.json().then((data) => {
          setPost(data);
        })
      )
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="mx-[66px] my-9">
      <img
        src={post ? `http://localhost:4000/${post.cover}` : ``}
        alt="img"
        className="h-[300px] w-screen "
      />
      <div className="my-8">
        <span className="font-bold text-5xl ">{post.title}</span>
      </div>
      <div className="flex gap-x-5">
        <div className="text-xl font-bold text-gray-500">-{post.author}</div>
        <div className="text-xl font-bold text-gray-500">
          <time>{}</time>
        </div>
      </div>
      <div className="my-8">
        <div
          className="text-2xl"
          dangerouslySetInnerHTML={{ __html: post.content }}
        ></div>
      </div>
    </div>
  );
};

export default Content;
