import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { formatISO9075 } from "date-fns";
const Content = () => {
  const { id } = useParams();
  const [post, setPost] = useState({});

  useEffect(() => {
    fetch(`http://localhost:4000/post/${id}`)
      .then((response) =>
        response.json().then((data) => {
          setPost(data);
        })
      )
      .catch((err) => {
        console.log(err);
      });
  }, []);
  console.log(new Date(post.createdAt).toISOString);
  return (
    <div className="mx-[66px] my-9">
      <img
        src={post ? `http://localhost:4000/${post.cover}` : ``}
        alt="img"
        className="h-[300px] w-screen "
      />

      <div className="my-8">
        <span className="font-bold text-5xl ">{post.title}</span>
      </div>
      <div className="text-xl font-bold text-gray-500">-{post.author}</div>
      <div className="text-xl font-bold text-gray-500">
        <time>{}</time>
      </div>
      <div className="my-8">
        <div
          className="text-2xl"
          dangerouslySetInnerHTML={{ __html: post.content }}
        ></div>
      </div>
    </div>
  );
};

export default Content;
